import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

interface Work {
  id: string;
  title: string;
  author: string;
  year: string;
  region: 'chinese' | 'western';
  description: string;
  cover: string;
  rating: number;
  reviews: string[];
}

const Works: React.FC = () => {
  const navigate = useNavigate();

  const works: Work[] = [
    {
      id: '1',
      title: '三体',
      author: '刘慈欣',
      year: '2008',
      region: 'chinese',
      description: '《三体》是刘慈欣创作的科幻小说，讲述了地球文明与三体文明的第一次接触，以及由此引发的一系列故事。',
      cover: '/covers/three-body.jpg',
      rating: 4.8,
      reviews: [
        '宏大的宇宙观和深刻的人性思考',
        '将中国科幻推向世界舞台的里程碑之作',
      ],
    },
    {
      id: '2',
      title: '基地',
      author: '艾萨克·阿西莫夫',
      year: '1951',
      region: 'western',
      description: '《基地》是阿西莫夫的科幻小说系列，描绘了一个跨越数千年的银河帝国兴衰史。',
      cover: '/covers/foundation.jpg',
      rating: 4.7,
      reviews: [
        '科幻文学史上的经典之作',
        '展现了宏大的历史观和人性洞察',
      ],
    },
    // 可以添加更多作品...
  ];

  return (
    <div className="container py-12">
      <h1 className="text-4xl font-bold text-primary mb-8">代表作品</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {works.map((work) => (
          <motion.div
            key={work.id}
            whileHover={{ scale: 1.05 }}
            className="bg-white rounded-lg shadow-lg overflow-hidden cursor-pointer"
            onClick={() => navigate(`/works/${work.id}`)}
          >
            <div className="aspect-w-16 aspect-h-9 bg-gray-200">
              <img
                src={work.cover}
                alt={work.title}
                className="w-full h-48 object-cover"
                onError={e => (e.currentTarget.style.display = 'none')}
              />
            </div>
            <div className="p-6">
              <h2 className="text-xl font-bold text-primary">{work.title}</h2>
              <p className="text-gray-600 mt-2">
                {work.author} · {work.year}
              </p>
              <div className="mt-4 flex items-center">
                <span className="text-yellow-400">★</span>
                <span className="ml-1 text-gray-600">{work.rating}</span>
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Works;
